sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {

    },

    isDatePast: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var rightnowDT = new GlideDateTime();
		var rightnow = rightnowDT.getDate();
        var testdateDT = new GlideDateTime(strDate);
        var testdate = testdateDT.getDate();
        var testdateToTestdateDT = new GlideDateTime(testdate);


        // If the testdate is before rightnow, return true, else return false
        if (rightnow.compareTo(testdate) > 0) {
            return true;
        } else {
            return false;
        }
    },

    isDateToday: function(strDate) {
        var todayDT = new GlideDateTime();
        var testDateDT = new GlideDateTime(strDate);
        var today = todayDT.getDate();
		var testDate = testDateDT.getDate();

		if(today.compareTo(testDate) === 0){
			return true;
		}
		else {
			return false;
		}
    },

    type: 'NeedItUtils'
};
